/* import logo from './logo.svg'; */  //eliminar las referencias de los archivos que eliminamos
import './App.css';
import { useState } from "react";
import Axios from "axios";

function App() {
  const[nombre,setNombre] = useState("");
  const[edad,setEdad] = useState(0);
  const[pais,setPais] = useState("");
  const[cargo,setCargo] = useState("");
  const[anios,setAnios] = useState(0);

  const [empleadosLista,setEmpleados] = useState([]); //iniaclizamos en una lista vacia

  const agregarEmpleado = () =>{
    //metodo de la solicitud     //colocamos al ruta
    Axios.post("http://localhost:3001/empleados/create",{    
      nombre: nombre,
      edad: edad,            //cuerpo del mensaje
      pais: pais,           //....
      cargo: cargo,
      anios: anios
    }).then(()=>{
      alert('persona registrada');
    });
  };

  const leerEmpleado = () =>{
    //metodo de la solicitud     //colocamos al ruta pra traer sus valores
    Axios.get("http://localhost:3001/empleados/read").then((response)=>{//cuando haya una respuesta la guardara en response 
      setEmpleados(response.data);//de esta manera vienen todos los datos que traemos desde la API
    });
  };


  return (
    <div className="App">
      <div className="datos" > {/* contenedor para mi formulario */}

        <label>
          Nombre:
          <input onChange={(event)=>{
            setNombre(event.target.value);
          }}  type="text"></input>
        </label>
        <label>
          Edad:
          <input onChange={(event)=>{
            setEdad(event.target.value);
          }}  type="number"></input>
        </label>
        <label>
          Pais:
          <input onChange={(event)=>{
            setPais(event.target.value);
          }}  type="text"></input>
        </label>
        <label>
          Cargo:
          <input onChange={(event)=>{
            setCargo(event.target.value);
          }}  type="text"></input>
        </label>
        <label>
          AÃ±os:
          <input onChange={(event)=>{
            setAnios(event.target.value);
          }}  type="number"></input>
        </label>
        <button onClick={agregarEmpleado} >Registrar</button>
      </div>
      <div className='lista'> {/*  creamos un nuevo divisor para el listado de los datos*/}

        <button onClick={leerEmpleado}>Lista</button>

        {  /* map nos ayudara a recorrer todos lo valores */
          empleadosLista.map((valor,key)=>{
            return (
            <div className=''>{valor.nombre} </div>,
            <div className=''>{valor.edad} </div>,
            <div className=''>{valor.pais} </div>,
            <div className=''>{valor.cargo} </div>,
            <div className=''>{valor.anios} </div>
            )
          })
        }

      </div>
    </div>
  );
}

export default App;
