import './App.css';
import { useState } from 'react';
import Axios from "axios";


function App() {

  const[codigo,setCodigo]= useState('');
  const[nombre,setNombre]= useState('');
  const[descripcion,setDescripcion]= useState('');
  const[fechaCompra,setFechaCompra]= useState('');
  const[valoCompra,setValorCompra]= useState(0);
  const[proveedor,setProveedor]= useState('');

  const agregarProducto = (event)=>{
    event.preventDefault(); 
    //metodo  de la solicutud
    Axios.post("http://localhost:3001/productos/crear",{
      codigo:codigo,
      nombre:nombre,
      descripcion:descripcion,
      fecha_Compra:fechaCompra,
      valor_compra:valoCompra,
      proveedor:proveedor
    }).then(()=>{
      alert('Productoi agregado satisfactoriamente');
    }).catch((error) => {
      console.error("Hubo un error al agregar el producto: ", error);
      alert('Ocurrió un error al agregar el producto');
    });
  };

  return (
    <div className="App">
      <form onSubmit={agregarProducto}>

        <label>Codigo:
          <input onChange={(event)=>{setCodigo(event.target.value)}} type='text'></input>
        </label>

        <label>Nombre:
          <input onChange={(event)=>{setNombre(event.target.value)}} type='text'></input>
        </label>

        <label>Descripcion:
          <input onChange={(event)=>{setDescripcion(event.target.value)}} type='text'></input>
        </label>

        <label>Fecha de Carga:
          <input onChange={(event)=>{setFechaCompra(event.target.value)}} type='date'></input>
        </label>

        <label>Valor Compra:
          <input onChange={(event)=>{setValorCompra(event.target.value)}} type='number'></input>
        </label>

        <label>Proveedor:
          <input onChange={(event)=>{setProveedor(event.target.value)}} type='text'></input>
        </label>

        <button type='submit'>Agregar</button>

      </form>
    </div>
  );
}

export default App;
